// src/components/Inventory/UnidadCard.tsx
import React from 'react';
import { Unidad, User } from '../../types';
import { formatDate } from '../../utils/dates';
import { usePermissions } from '../../utils/permissions';

interface UnidadCardProps {
  unidad: Unidad;
  user: User | null;
  stockCount?: number;
  stockActual?: number;
  unidadesAgotadas?: number;
  pesoVendido?: number;
  onEdit?: (unidad: Unidad) => void;
  onCut?: (unidad: Unidad) => void;
  onDelete?: (unidad: Unidad) => void;
  isHistorial: boolean;
  vistaMode?: 'lista' | 'grid';
}

export const UnidadCard: React.FC<UnidadCardProps> = ({
  unidad,
  user,
  stockCount,
  stockActual,
  unidadesAgotadas,
  pesoVendido,
  onEdit,
  onCut,
  onDelete,
  isHistorial,
  vistaMode = 'lista',
}) => {
  const { canDelete, canEdit, canCut } = usePermissions(user);

  const getTipoBadgeClass = (tipo: string) => {
    const tipos: { [key: string]: string } = {
      'blando': 'badge-blando',
      'semi-duro': 'badge-semi-duro',
      'duro': 'badge-duro',
    };
    return tipos[tipo?.toLowerCase()] || 'badge-blando';
  };

  const isGrid = vistaMode === 'grid';

  return (
    <div className={`unit-card ${vistaMode}`}>  {/* ‚Üê APLICAR CLASE AQU√ç */}
      <div className="unit-header" style={isGrid ? { flexDirection: 'column', gap: '0.75rem' } : {}}>
        <div className="unit-info" style={{ width: '100%' }}>
          <div className="unit-title-row" style={isGrid ? { flexWrap: 'wrap', marginBottom: '0.5rem' } : {}}>
            <span className={`badge ${getTipoBadgeClass(unidad.producto.tipoQueso.nombre)}`}>
              {unidad.producto.tipoQueso.nombre}
            </span>
            <span className="badge badge-plu">PLU: {unidad.producto.plu}</span>
            <span className={`badge ${unidad.activa ? 'badge-status' : 'badge-inactive'}`}>
              {unidad.activa ? 'Activa' : 'Agotada'}
            </span>
            
            {isHistorial && stockActual !== undefined && (
              <span className="badge" style={{ background: '#e0e7ff', color: '#4338ca' }}>
                Stock: {stockActual}
              </span>
            )}
            
            {!isHistorial && unidad.activa && stockCount !== undefined && (
              <span className="badge" style={{ background: '#e0e7ff', color: '#4338ca' }}>
                Stock: {stockCount}
              </span>
            )}
          </div>
          
          <h3 className="unit-name" style={isGrid ? { fontSize: '1.1rem', margin: '0.5rem 0' } : {}}>
            {unidad.producto.nombre}
          </h3>
          
          <div className="unit-id">ID: #{unidad.id}</div>
          
          {isHistorial && !unidad.activa && unidadesAgotadas !== undefined && pesoVendido !== undefined && (
            <div style={{ marginTop: '0.5rem', fontSize: '0.875rem', color: '#6b7280' }}>
              Agotadas: {unidadesAgotadas} | Vendido: {(pesoVendido / 1000).toFixed(1)}kg
            </div>
          )}
        </div>

        {unidad.activa && !isHistorial && (
          <div className="unit-actions" style={isGrid ? { width: '100%', justifyContent: 'stretch', gap: '0.5rem' } : {}}>
            {canCut && onCut && (
              <button
                className="btn-action btn-cut"
                onClick={() => onCut(unidad)}
                style={isGrid ? { flex: 1, justifyContent: 'center' } : {}}
              >
                ‚úÇÔ∏è {isGrid && 'Cortar'}
              </button>
            )}
            
            {canEdit && onEdit && (
              <button
                className="btn-action btn-edit"
                onClick={() => onEdit(unidad)}
                style={isGrid ? { flex: 1, justifyContent: 'center' } : {}}
              >
                ‚úèÔ∏è {isGrid && 'Editar'}
              </button>
            )}

            {canDelete && onDelete && (
              <button 
                className="btn-action btn-delete" 
                onClick={() => onDelete(unidad)}
                title="Eliminar unidad"
                style={isGrid ? { flex: 1, justifyContent: 'center' } : {}}
              >
                üóëÔ∏è {isGrid && 'Eliminar'}
              </button>
            )}
          </div>
        )}
      </div>

      <div className="unit-details" style={isGrid ? { 
        display: 'grid', 
        gridTemplateColumns: 'repeat(2, 1fr)', 
        gap: '0.75rem',
        marginTop: '1rem'
      } : {}}>
        <div className="detail-item">
          <div className="detail-label">Peso Inicial</div>
          <div className="detail-value">{unidad.pesoInicial}g</div>
        </div>
        <div className="detail-item">
          <div className="detail-label">Peso Actual</div>
          <div className="detail-value highlight">{unidad.pesoActual}g</div>
        </div>
        <div className="detail-item">
          <div className="detail-label">Cortado</div>
          <div className="detail-value">{Number(unidad.pesoInicial) - Number(unidad.pesoActual)}g</div>
        </div>
        <div className="detail-item">
          <div className="detail-label">Ingresado</div>
          <div className="detail-value" style={{ fontSize: '0.875rem' }}>
            {formatDate(unidad.createdAt)}
          </div>
        </div>
      </div>

      {unidad.motivo && (
        <div className="observaciones-section" style={isGrid ? { marginTop: '0.75rem', padding: '0.5rem' } : {}}>
          <div className="observaciones-title" style={isGrid ? { fontSize: '0.75rem' } : {}}>Motivo</div>
          <div className="observaciones-content" style={isGrid ? { fontSize: '0.875rem' } : {}}>
            {unidad.motivo.nombre}
          </div>
        </div>
      )}

      {unidad.observacionesIngreso && (
        <div className="observaciones-section" style={isGrid ? { marginTop: '0.5rem', padding: '0.5rem' } : {}}>
          <div className="observaciones-title" style={isGrid ? { fontSize: '0.75rem' } : {}}>Observaciones</div>
          <div className="observaciones-content" style={isGrid ? { fontSize: '0.875rem' } : {}}>
            {unidad.observacionesIngreso}
          </div>
        </div>
      )}

      {unidad.particiones && unidad.particiones.length > 0 && (
        <div className="partitions-section" style={isGrid ? { marginTop: '0.75rem' } : {}}>
          <div className="partitions-header">
            <div className="partitions-title" style={isGrid ? { fontSize: '0.8rem' } : {}}>
              Cortes ({unidad.particiones.length})
            </div>
          </div>
          {!isGrid && (
            <div className="partitions-list">
              {unidad.particiones.slice(0, 3).map((particion, idx) => (
                <div key={particion.id} className="partition-item">
                  <div className="partition-number">#{idx + 1}</div>
                  <div className="partition-weight">{particion.peso}g</div>
                </div>
              ))}
              {unidad.particiones.length > 3 && (
                <div style={{ fontSize: '0.75rem', color: '#6b7280', textAlign: 'center' }}>
                  +{unidad.particiones.length - 3} m√°s
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};
