// src/components/Dashboard/Dashboard.tsx
import React, { useEffect, useState } from 'react';
import {
  LineChart, Line, BarChart, Bar, PieChart, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import { User } from '../../types';

interface DashboardProps {
  user: User;
  onVolver: () => void;
}

interface DashboardData {
  inventarioActual: any[];
  ventas: {
    hoy: any[];
    semana: any[];
    mes: any[];
  };
  topProductos: any[];
  inventarioValorizado: any[];
  alertas: any[];
}



export const Dashboard: React.FC<DashboardProps> = ({ user, onVolver }) => {
  const [data, setData] = useState<DashboardData | null>(null);
  const [loading, setLoading] = useState(true);
  const [periodo, setPeriodo] = useState<'hoy' | 'semana' | 'mes'>('semana');

  useEffect(() => {
    fetchDashboard();
  }, []);

  const fetchDashboard = async () => {
    try {
      const [dashboardRes, alertasRes] = await Promise.all([
        fetch(`${process.env.REACT_APP_API_URL}/api/reportes/dashboard`, {
          headers: { Authorization: `Bearer ${user.token}` }
        }),
        fetch(`${process.env.REACT_APP_API_URL}/api/alertas`, {
          headers: { Authorization: `Bearer ${user.token}` }
        })
      ]);

      const dashboardData = await dashboardRes.json();
      const alertasData = await alertasRes.json();

      setData({
        ...dashboardData,
        alertas: alertasData
      });
    } catch (error) {
      console.error('Error al cargar dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!data) return null;

  const COLORS = ['#f59e0b', '#10b981', '#3b82f6', '#ef4444', '#8b5cf6'];

  // ‚úÖ FIX: Agregar optional chaining y valores por defecto
  const ventasPeriodo = data.ventas?.[periodo] || [];
  const inventarioActual = data.inventarioActual || [];
  const topProductos = data.topProductos || [];
  const inventarioValorizado = data.inventarioValorizado || [];
  const alertas = data.alertas || [];

  // Preparar datos para gr√°ficos con seguridad
  const ventasData = ventasPeriodo.map(v => ({
    fecha: v.fecha ? new Date(v.fecha).toLocaleDateString('es-AR', { 
      day: '2-digit', 
      month: 'short' 
    }) : '',
    peso: (parseFloat(v.totalPeso) / 1000).toFixed(2),
    cortes: parseInt(v.cantidadCortes)
  }));

  const inventarioPorTipo = inventarioActual.map(inv => ({
    name: inv.tipoQueso || 'Desconocido',
    value: parseFloat(inv.pesoTotal) / 1000 || 0
  }));

  // Calcular KPIs con seguridad
  const totalUnidades = inventarioActual.reduce((sum, inv) => 
    sum + (parseInt(inv.cantidad) || 0), 0
  );
  
  const totalPeso = inventarioActual.reduce((sum, inv) => 
    sum + (parseFloat(inv.pesoTotal) || 0), 0
  );
  
  const valorTotal = inventarioValorizado.reduce((sum, inv) => 
    sum + (parseFloat(inv.valorTotal) || 0), 0
  );
  
  const totalCortes = ventasPeriodo.reduce((sum, v) => 
    sum + (parseInt(v.cantidadCortes) || 0), 0
  );

  return (
    <div className="dashboard-container">
      {/* Header (igual) */}
      <div className="dashboard-header">
        <div>
          <h1>üìä Dashboard Anal√≠tico</h1>
          <button 
            className="btn-back" 
            onClick={onVolver}
            style={{ marginTop: '0.5rem' }}
          >
            ‚Üê Volver al Inventario
          </button>
        </div>
        <div className="periodo-selector">
          {['hoy', 'semana', 'mes'].map((p) => (
            <button 
              key={p}
              className={periodo === p ? 'active' : ''}
              onClick={() => setPeriodo(p as any)}
            >
              {p.charAt(0).toUpperCase() + p.slice(1)}
            </button>
          ))}
        </div>
      </div>

      {/* Alertas con verificaci√≥n */}
      {alertas.length > 0 && (
        <div className="alertas-section">
          <h3>‚ö†Ô∏è Alertas Activas ({alertas.length})</h3>
          <div className="alertas-grid">
            {alertas.slice(0, 3).map((alerta, idx) => (
              <div 
                key={idx} 
                className={`alerta-card ${alerta.prioridad || 'baja'}`}
              >
                <div className="alerta-header">
                  <span className="alerta-tipo">{alerta.tipo || 'Info'}</span>
                  <span className="alerta-prioridad">{alerta.prioridad || 'baja'}</span>
                </div>
                <p>{alerta.mensaje}</p>
                {alerta.detalles && (
                  <div className="alerta-detalles">
                    {Object.entries(alerta.detalles).map(([key, value]) => (
                      <span key={key}>
                        {key}: <strong>{String(value)}</strong>
                      </span>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* KPIs con valores seguros */}
      <div className="kpis-grid">
        <div className="kpi-card">
          <div className="kpi-icon">üì¶</div>
          <div className="kpi-content">
            <div className="kpi-value">{totalUnidades}</div>
            <div className="kpi-label">Unidades en Stock</div>
          </div>
        </div>

        <div className="kpi-card">
          <div className="kpi-icon">‚öñÔ∏è</div>
          <div className="kpi-content">
            <div className="kpi-value">{(totalPeso / 1000).toFixed(1)} kg</div>
            <div className="kpi-label">Peso Total</div>
          </div>
        </div>

        <div className="kpi-card">
          <div className="kpi-icon">üí∞</div>
          <div className="kpi-content">
            <div className="kpi-value">${valorTotal.toFixed(2)}</div>
            <div className="kpi-label">Valor Inventario</div>
          </div>
        </div>

        <div className="kpi-card">
          <div className="kpi-icon">‚úÇÔ∏è</div>
          <div className="kpi-content">
            <div className="kpi-value">{totalCortes}</div>
            <div className="kpi-label">Cortes ({periodo})</div>
          </div>
        </div>
      </div>

      {/* Gr√°ficos con datos seguros */}
      <div className="charts-grid">
        {/* Ventas por d√≠a */}
        <div className="chart-card">
          <h3>Ventas en el Per√≠odo</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={ventasData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="fecha" />
              <YAxis yAxisId="left" />
              <YAxis yAxisId="right" orientation="right" />
              <Tooltip />
              <Legend />
              <Line 
                yAxisId="left"
                type="monotone" 
                dataKey="peso" 
                stroke="#f59e0b" 
                name="Peso (kg)"
                strokeWidth={2}
              />
              <Line 
                yAxisId="right"
                type="monotone" 
                dataKey="cortes" 
                stroke="#10b981" 
                name="Cortes"
                strokeWidth={2}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Top productos */}
        <div className="chart-card">
          <h3>Top 5 Productos Vendidos</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={topProductos.slice(0, 5)}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="nombre" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="totalVendido" fill="#f59e0b" name="Peso Vendido (g)" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Inventario por tipo */}
        <div className="chart-card">
          <h3>Inventario por Tipo de Queso</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={inventarioPorTipo}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, value }) => `${name}: ${value.toFixed(1)}kg`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
              >
                {inventarioPorTipo.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Inventario valorizado */}
        <div className="chart-card">
          <h3>Valor del Inventario por Producto</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={inventarioValorizado}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="producto" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="valorTotal" fill="#10b981" name="Valor ($)" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Tabla de productos */}
      <div className="productos-table-card">
        <h3>Detalle de Productos M√°s Vendidos</h3>
        <table className="dashboard-table">
          <thead>
            <tr>
              <th>Producto</th>
              <th>Total Vendido</th>
              <th>Cantidad Cortes</th>
              <th>Promedio por Corte</th>
            </tr>
          </thead>
          <tbody>
            {topProductos.map((prod, idx) => (
              <tr key={idx}>
                <td>{prod.nombre}</td>
                <td>{(parseFloat(prod.totalVendido) / 1000).toFixed(2)} kg</td>
                <td>{prod.cantidadCortes}</td>
                <td>{(parseFloat(prod.promedioCorte)).toFixed(0)} g</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Botones de exportaci√≥n */}
      <div className="export-buttons">
        <button 
          className="btn-export"
          onClick={() => window.open(
            `${process.env.REACT_APP_API_URL}/api/export/inventario/excel`,
            '_blank'
          )}
        >
          üìä Exportar Inventario (Excel)
        </button>
        <button 
          className="btn-export"
          onClick={() => window.open(
            `${process.env.REACT_APP_API_URL}/api/export/inventario/pdf`,
            '_blank'
          )}
        >
          üìÑ Exportar Inventario (PDF)
        </button>
      </div>
    </div>
  );
};
