// src/components/Admin/AdminPanel.tsx
import React from 'react';
import { ProductosManager } from './ProductosManager';
import { Producto, TipoQueso, CreateProductoData } from '../../types';

interface AdminPanelProps {
  productos: Producto[];
  tiposQueso: TipoQueso[];
  loading: boolean;
  error: string;
  success: string;
  onCreateProducto: (data: CreateProductoData) => Promise<{ success: boolean }>;
  onUpdateProducto: (id: number, data: Partial<CreateProductoData>) => Promise<{ success: boolean }>;
  onDeleteProducto: (id: number) => Promise<{ success: boolean }>;
  onClose: () => void;
}

export const AdminPanel: React.FC<AdminPanelProps> = ({
  productos,
  tiposQueso,
  loading,
  error,
  success,
  onCreateProducto,
  onUpdateProducto,
  onDeleteProducto,
  onClose,
}) => {
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div 
        className="modal historial-modal" 
        onClick={(e) => e.stopPropagation()}
        style={{ maxWidth: '1000px' }}
      >
        <div className="modal-header">
          <h3 className="modal-title">⚙️ Panel de Administración</h3>
          <button className="btn-close" onClick={onClose}>✕</button>
        </div>

        <div style={{ marginBottom: '1rem' }}>
          <div style={{ 
            padding: '1rem', 
            background: '#fef3c7', 
            borderRadius: '8px',
            borderLeft: '4px solid #f59e0b'
          }}>
            <strong>Modo Administrador</strong>
            <p style={{ margin: '0.5rem 0 0 0', fontSize: '0.875rem', color: '#92400e' }}>
              Ten cuidado con las eliminaciones. Esta área está reservada para usuarios con rol de administrador.
            </p>
          </div>
        </div>

        <ProductosManager
          productos={productos}
          tiposQueso={tiposQueso}
          loading={loading}
          error={error}
          success={success}
          onCreate={onCreateProducto}
          onUpdate={onUpdateProducto}
          onDelete={onDeleteProducto}
        />
      </div>
    </div>
  );
};
